mapper.sql:
	FROM member a 
	LEFT JOIN product b on b.member_memberSeq = a.memberSeq
	WHERE 1=1
 AND a.memberUseNy= #{shUseNy}
 AND a.memberDelNy = #{shDelNy}

	AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}


	AND a.memberSeq = #{shValue}
	AND a.memberName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO member (
memberName
,memberUseNy
,memberOrder
,memberDesc
,memberDelNy
,regDateTime
,modDateTime
,memberGender
) VALUES (
#{memberName}
,#{memberUseNy}
,#{memberOrder}
,#{memberDesc}
,0
,now()
,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
SET
memberName = #{memberName}
,memberUseNy = #{memberUseNy}
,memberOrder = #{memberOrder}
WHERE 1=1
AND memberSeq
= #{memberSeq}
