mapper.sql:
	FROM product a 
	LEFT JOIN member b on a.member_memberSeq = b.memberSeq
	WHERE 1=1
 AND a.productUseNy= #{shUseNy}
 AND a.productDelNy = #{shDelNy}

	AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}


	AND a.productSeq = #{shValue}
	AND a.productName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO product (
productName
,productUseNy
,productOrder
,productDesc
,productDelNy
,productGender
,regDateTime
,modDateTime
) VALUES (
#{productName}
,#{productUseNy}
,#{productOrder}
,#{productDesc}
,0
,now()
,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE product
SET
productName = #{productName}
,productUseNy = #{productUseNy}
,productOrder = #{productOrder}
WHERE 1=1
AND productSeq
= #{productSeq}
